// src/components/User/OrderHistory.tsx

import React, { useState, useEffect } from 'react';
import api from '../../api/index.ts';
import { Order } from '../../types/Order.ts'; // You might need to define this type!

// Define the Order type if you haven't already (in src/types/Order.ts)
/*
export interface Order {
    id: number;
    user_id: number;
    total_price: number;
    status: string; // e.g., 'Pending', 'Completed'
    created_at: string; // Date string
    order_items: OrderItem[];
}

export interface OrderItem {
    sweet_id: number;
    quantity: number;
    price_at_purchase: number; // Price when the order was placed
    sweet_name?: string; // We'll add this from the frontend later for display
}
*/


const OrderHistory: React.FC = () => {
    const [orders, setOrders] = useState<Order[]>([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchOrders = async () => {
            try {
                // GET /api/orders/ returns orders for the logged-in user
                const response = await api.get<Order[]>('/orders/');
                setOrders(response.data);
            } catch (err: any) {
                console.error("Failed to fetch orders:", err);
                setError("Failed to load order history. Please try again.");
            } finally {
                setLoading(false);
            }
        };

        fetchOrders();
    }, []);

    if (loading) {
        return <p>Loading your order history...</p>;
    }

    if (error) {
        return <div className="alert alert-danger">{error}</div>;
    }

    return (
        <div className="container mt-5">
            <h2 className="mb-4">üõçÔ∏è Your Order History</h2>

            {orders.length === 0 ? (
                <div className="alert alert-info">You have not placed any orders yet.</div>
            ) : (
                <div className="list-group">
                    {orders.map((order) => (
                        <div key={order.id} className="card shadow mb-4">
                            <div className="card-header bg-success text-white">
                                <h5 className="mb-0">Order ID: **{order.id}** </h5>
                            </div>
                            <div className="card-body">
                                <p><strong>Date Placed:</strong> {new Date(order.created_at).toLocaleDateString()}</p>
                                <p><strong>Status:</strong> {order.status}</p>
                                <p className="h4"><strong>Total:</strong> ‚Çπ{order.total_price.toFixed(2)}</p>

                                <h6 className="mt-3">Items Purchased:</h6>
                                <ul className="list-group list-group-flush">
                                    {order.order_items.map((item, index) => (
                                        // NOTE: You might need to fetch the sweet name separately 
                                        // unless your backend already sends it back in the order_items payload.
                                        // For now, we'll use sweet_id as a placeholder.
                                        <li key={index} className="list-group-item d-flex justify-content-between align-items-center">
                                            <span>Sweet ID: {item.sweet_id}</span>
                                            <span>Quantity: **{item.quantity}**</span>
                                            <span className="fw-bold">Price: ‚Çπ{(item.price_at_purchase * item.quantity).toFixed(2)}</span>
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default OrderHistory;
