// src/components/CartSummary.tsx
import React from 'react';

// --- Type Definitions ---
interface CartItem {
    sweet_id: number;
    name: string;
    price_at_purchase: number;
    quantity: number;
}

interface CartSummaryProps {
    cart: CartItem[];
    setCart: React.Dispatch<React.SetStateAction<CartItem[]>>;
    placeOrder: () => void; 
}

const CartSummary: React.FC<CartSummaryProps> = ({ cart, setCart, placeOrder }) => {

    const calculateTotal = () => {
        return cart.reduce((total, item) => total + (item.price_at_purchase * item.quantity), 0);
    };

    const updateQuantity = (sweetId: number, delta: number) => {
        setCart(prevCart => {
            return prevCart
                .map(item => 
                    item.sweet_id === sweetId 
                        ? { ...item, quantity: item.quantity + delta } 
                        : item
                )
                .filter(item => item.quantity > 0);
        });
    };

    return (
        <div className="card shadow-sm sticky-top" style={{ top: '6rem' }}>
            <div className="card-header bg-primary text-white">
                <h4 className="mb-0">ðŸ›’ Your Cart</h4>
            </div>
            <div className="card-body">
                {cart.length === 0 ? (
                    <p className="text-center text-muted">Cart is empty. Add some delicious sweets!</p>
                ) : (
                    <ul className="list-group list-group-flush">
                        {cart.map(item => (
                            <li 
                                key={item.sweet_id} 
                                className="list-group-item d-flex justify-content-between align-items-center"
                            >
                                <div>
                                    <span className="fw-bold">{item.name}</span>
                                    <div className="text-muted small">
                                        â‚¹{item.price_at_purchase.toFixed(2)} x {item.quantity}
                                    </div>
                                </div>
                                <div className="d-flex align-items-center">
                                    <button 
                                        className="btn btn-sm btn-outline-secondary me-2"
                                        onClick={() => updateQuantity(item.sweet_id, -1)}
                                        disabled={item.quantity <= 1} 
                                    >
                                        -
                                    </button>
                                    <span className="fw-bold me-2">{item.quantity}</span>
                                    <button 
                                        className="btn btn-sm btn-outline-secondary"
                                        onClick={() => updateQuantity(item.sweet_id, 1)}
                                    >
                                        +
                                    </button>
                                </div>
                            </li>
                        ))}
                    </ul>
                )}
            </div>
            
            <div className="card-footer">
                <div className="d-flex justify-content-between fw-bold mb-3">
                    <span>Total:</span>
                    <span className="text-success">â‚¹{calculateTotal().toFixed(2)}</span>
                </div>
                <button 
                    className="btn btn-lg btn-success w-100"
                    onClick={placeOrder}
                    disabled={cart.length === 0}
                >
                    Place Order
                </button>
                <button 
                    className="btn btn-sm btn-link text-danger w-100 mt-2"
                    onClick={() => setCart([])}
                    disabled={cart.length === 0}
                >
                    Clear Cart
                </button>
            </div>
        </div>
    );
};

export default CartSummary;
