// src/components/Admin/CreateSweetForm.tsx

import React, { useState } from 'react';
import api from '../../api/index.ts'; // Your configured Axios instance
import { Sweet } from '../../types/Sweet.ts'; // We'll define this type next

const CreateSweetForm: React.FC = () => {
    const [name, setName] = useState('');
    const [price, setPrice] = useState(''); // Use string for input, convert to number later
    const [description, setDescription] = useState('');
    const [error, setError] = useState<string | null>(null);
    const [successMessage, setSuccessMessage] = useState<string | null>(null);
    const [loading, setLoading] = useState(false);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setError(null);
        setSuccessMessage(null);
        setLoading(true);

        // Basic validation and conversion
        const sweetPrice = parseFloat(price);
        if (isNaN(sweetPrice) || sweetPrice <= 0) {
            setError('Please enter a valid price.');
            setLoading(false);
            return;
        }

        try {
            const sweetData = {
                name,
                price: sweetPrice,
                description,
            };

            // Call the new backend endpoint: POST /api/sweets/
            const response = await api.post<Sweet>('/sweets/', sweetData);
            
            setSuccessMessage(`Sweet "${response.data.name}" added successfully!`);
            
            // Clear the form fields after successful creation
            setName('');
            setPrice('');
            setDescription('');

        } catch (err: any) {
            let errorMessage = 'An unexpected error occurred during creation.';
            
            // Handle API errors (e.g., 400 Bad Request, 403 Forbidden)
            if (err.response && err.response.data && err.response.data.detail) {
                // If the error is a string (FastAPI), use it directly
                if (typeof err.response.data.detail === 'string') {
                    errorMessage = err.response.data.detail;
                } 
                // If it's a 422 validation error (less likely here but good practice)
                else if (Array.isArray(err.response.data.detail)) {
                    errorMessage = `Validation Error: ${err.response.data.detail[0]?.msg || 'Invalid data.'}`;
                }
            }
            
            setError(errorMessage);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="card shadow mb-4">
            <div className="card-header bg-primary text-white">
                <h5 className="mb-0">Add New Sweet to Inventory</h5>
            </div>
            <div className="card-body">
                {error && <div className="alert alert-danger">{error}</div>}
                {successMessage && <div className="alert alert-success">{successMessage}</div>}

                <form onSubmit={handleSubmit}>
                    <div className="mb-3">
                        <label htmlFor="sweetName" className="form-label">Sweet Name</label>
                        <input
                            type="text"
                            className="form-control"
                            id="sweetName"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            required
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="sweetPrice" className="form-label">Price (â‚¹)</label>
                        <input
                            type="number"
                            className="form-control"
                            id="sweetPrice"
                            value={price}
                            onChange={(e) => setPrice(e.target.value)}
                            step="0.01"
                            min="0.01"
                            required
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="sweetDescription" className="form-label">Description</label>
                        <textarea
                            className="form-control"
                            id="sweetDescription"
                            rows={3}
                            value={description}
                            onChange={(e) => setDescription(e.target.value)}
                            required
                        />
                    </div>
                    <button type="submit" className="btn btn-primary" disabled={loading}>
                        {loading ? 'Adding...' : 'Create Sweet'}
                    </button>
                </form>
            </div>
        </div>
    );
};

export default CreateSweetForm;
