// src/components/User/OrderHistory.tsx (FIXED)

import React, { useState, useEffect } from 'react';
import api from '../../api/index.ts';
import { Order } from '../../types/Order.ts'; 

// ... (Other code remains the same)

const OrderHistory: React.FC = () => {
    // ... (State definitions remain the same)
    const [orders, setOrders] = useState<Order[]>([]); // Initialized to an empty array
    // ...

    useEffect(() => {
        const fetchOrders = async () => {
            try {
                // Ensure we handle cases where the data property might be null/undefined
                const response = await api.get<Order[]>('/orders/');
                
                // IMPORTANT: Use optional chaining and nullish coalescing for safety
                setOrders(response.data ?? []); // <-- Safety check ensures 'orders' is an array
                
            } catch (err: any) {
                console.error("Failed to fetch orders:", err);
                setError("Failed to load order history. Please try again.");
            } finally {
                setLoading(false);
            }
        };

        fetchOrders();
    }, []);

    // ... (Loading and Error handling remains the same)

    return (
        <div className="container mt-5">
            <h2 className="mb-4">üõçÔ∏è Your Order History</h2>

            {/* ADD A CHECK HERE: Ensure orders is an array AND has length 0 */}
            {(!orders || orders.length === 0) ? (
                <div className="alert alert-info">You have not placed any orders yet.</div>
            ) : (
                <div className="list-group">
                    
                    {/* DEFENSIVE CHECK: Use optional chaining to safely map */}
                    {orders?.map((order) => (  // <-- FIX APPLIED HERE: orders?.map
                        <div key={order.id} className="card shadow mb-4">
                            {/* ... (Rest of the rendering code remains the same) */}
                            <div className="card-header bg-success text-white">
                                <h5 className="mb-0">Order ID: **{order.id}** </h5>
                            </div>
                            <div className="card-body">
                                <p><strong>Date Placed:</strong> {new Date(order.created_at).toLocaleDateString()}</p>
                                <p><strong>Status:</strong> {order.status}</p>
                                <p className="h4"><strong>Total:</strong> ‚Çπ{order.total_price.toFixed(2)}</p>

                                <h6 className="mt-3">Items Purchased:</h6>
                                <ul className="list-group list-group-flush">
                                    {/* ANOTHER DEFENSIVE CHECK: orders.order_items might be null/undefined if the backend has an issue */}
                                    {order.order_items?.map((item, index) => ( // <-- FIX APPLIED HERE: order.order_items?.map
                                        <li key={index} className="list-group-item d-flex justify-content-between align-items-center">
                                            <span>Sweet ID: {item.sweet_id}</span>
                                            <span>Quantity: **{item.quantity}**</span>
                                            <span className="fw-bold">Price: ‚Çπ{(item.price_at_purchase * item.quantity).toFixed(2)}</span>
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default OrderHistory;
