// src/App.tsx
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext';
import useAuth from './hooks/useAuth'; // Assuming you have this hook

// Components
import Header from './components/Header';
import LoginForm from './components/Auth/LoginForm';
import RegisterForm from './components/Auth/RegisterForm';
import Dashboard from './components/Dashboard'; // NEW: The main User Dashboard
import SweetForm from './components/Admin/SweetForm'; // Admin Component

// --- Helper Component: PrivateRoute ---
// This ensures that routes are only accessible to logged-in users.
const PrivateRoute = ({ element }: { element: React.ReactNode }) => {
    const { isAuthenticated, user } = useAuth(); // Assume useAuth provides isAuthenticated and user
    
    // Check if the user is authenticated
    if (!isAuthenticated) {
        return <Navigate to="/login" replace />;
    }
    
    // Render the requested element if authenticated
    return element;
};

// --- Helper Component: AdminRoute ---
// Ensures only Admins can access routes like SweetForm.
const AdminRoute = ({ element }: { element: React.ReactNode }) => {
    const { isAuthenticated, user } = useAuth();
    
    if (!isAuthenticated) {
        return <Navigate to="/login" replace />;
    }
    
    // Check if the user object exists and has the is_admin flag set
    if (!user || !user.is_admin) {
        // Redirect non-admins to the standard dashboard
        return <Navigate to="/" replace />; 
    }
    
    return element;
};


const App: React.FC = () => {
    return (
        <Router>
            <AuthProvider>
                <Header />
                <main className="container-fluid py-4">
                    <Routes>
                        {/* Public Routes */}
                        <Route path="/login" element={<LoginForm />} />
                        <Route path="/register" element={<RegisterForm />} />
                        
                        {/* Private Routes (Requires Login) */}
                        {/* Default route after login is now the Dashboard */}
                        <Route 
                            path="/" 
                            element={<PrivateRoute element={<Dashboard />} />} 
                        />

                        {/* Admin Routes (Requires Admin privileges) */}
                        <Route 
                            path="/admin/sweets" 
                            element={<AdminRoute element={<SweetForm />} />} 
                        />

                        {/* Fallback route */}
                        <Route path="*" element={<Navigate to="/" replace />} />
                    </Routes>
                </main>
            </AuthProvider>
        </Router>
    );
};

export default App;
